mapper.update:
update imagebbs_tbl set title=#{title}, 
content=#{content}, imagename=#{imagename} 
where w_id=#{w_id}

mapper.select:
select w_id, group_id, parent_id, order_no, 
	title, writer, to_char(w_date,'YYYY/MM/DD HH24:MI:SS') as w_date,
	imagename, content, password from imagebbs_tbl where w_id = #{id}

select w_id, title, writer, w_date, imagename
from (select rownum rank, a.*
        from(
        	select w_id, title, writer, 
	     	to_char(w_date,'YYYY/MM/DD HH24:MI:SS') as "W_DATE", imagename
	     	from imagebbs_tbl 
	     	order by group_id desc, order_no asc
	     	) a)
where rank > #{start} and rank < #{end}
